import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  PieChart, Pie, Cell, ResponsiveContainer
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setExpenses(sortedExpenses);

      const total = sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0);
      setTotalExpenses(total);

      const categoryMap = {};
      sortedExpenses.forEach(expense => {
        categoryMap[expense.category] = (categoryMap[expense.category] || 0) + expense.amount;
      });

      const categoryData = Object.entries(categoryMap).map(([name, value]) => ({
        name,
        value: parseFloat(value.toFixed(2))
      }));
      setCategoryTotals(categoryData);

      const monthlyMap = {};
      sortedExpenses.forEach(expense => {
        const month = new Date(expense.date).toLocaleString('default', { month: 'long' });
        monthlyMap[month] = (monthlyMap[month] || 0) + expense.amount;
      });

      const monthlyData = Object.entries(monthlyMap).map(([month, amount]) => ({
        month,
        amount: parseFloat(amount.toFixed(2))
      }));
      setMonthlyExpenses(monthlyData);

      setLoading(false);
    } catch (err) {
      console.error("Error fetching expenses:", err);
      setError(err.response?.data?.message || "Failed to fetch expenses");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  const handleDeleteExpense = async (expenseId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_BASE_URL}/finance/expenses/${expenseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchExpenses();
    } catch (err) {
      setError("Failed to delete expense");
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="flex">
        <Sidebar />
        <div className="flex-1 p-6">
          <div className="text-center animate-fade-in">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading your financial data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 p-6 bg-gray-100 min-h-screen overflow-x-hidden animate-fade-in">
        <div className="max-w-7xl mx-auto">

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          {/* Overview Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            {[
              { title: "Total Expenses", value: formatCurrency(totalExpenses), color: "blue" },
              { title: "Categories", value: categoryTotals.length, color: "green" },
              { title: "This Month", value: formatCurrency(monthlyExpenses[monthlyExpenses.length - 1]?.amount || 0), color: "purple" }
            ].map((card, index) => (
              <div key={index} className={`bg-white rounded-lg shadow-lg p-6 transition-transform transform hover:scale-105 border-t-4 border-${card.color}-500`}>
                <h3 className="text-lg font-semibold text-gray-600">{card.title}</h3>
                <p className={`text-3xl font-bold text-${card.color}-600`}>{card.value}</p>
              </div>
            ))}
          </div>

          {/* Charts */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
            <div className="bg-white rounded-lg shadow-lg p-6 transition hover:shadow-2xl">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Expense by Category</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={categoryTotals} cx="50%" cy="50%" outerRadius={80} fill="#8884d8" dataKey="value" label={({ name, value }) => `${name}: ${formatCurrency(value)}`}>
                    {categoryTotals.map((entry, index) => <Cell key={index} fill={COLORS[index % COLORS.length]} />)}
                  </Pie>
                  <Tooltip formatter={(value) => formatCurrency(value)} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-6 transition hover:shadow-2xl">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Monthly Expenses</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={monthlyExpenses}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis tickFormatter={(value) => formatCurrency(value)} />
                  <Tooltip formatter={(value) => formatCurrency(value)} />
                  <Legend />
                  <Bar dataKey="amount" fill="#8884d8" name="Amount" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Add Expense Button */}
          <div className="text-center">
            <button onClick={() => navigate("/add-expense")} className="bg-blue-600 text-white px-6 py-2 rounded-lg transition-transform transform hover:scale-110 hover:bg-blue-700 shadow-md">
              Add Expense
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
