import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";
import "../styles/Sidebar.css";

const Sidebar = () => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <>
      {/* Menu Button (Always Visible in Top Left) */}
      <button
        className="menu-button"
        onClick={() => setIsOpen(true)}
      >
        <FaBars />
      </button>

      {/* Sidebar (Only Visible When isOpen is True) */}
      {isOpen && (
        <div className="sidebar-overlay">
          <div className="sidebar">
            {/* Close Button */}
            <button className="close-btn" onClick={() => setIsOpen(false)}>
              <FaTimes />
            </button>

            {/* Sidebar Content */}
            <div className="sidebar-logo">
              <h2>Finance Tracker</h2>
            </div>
            <ul className="sidebar-list">
              <li>
                <Link to="/dashboard" className="sidebar-link" onClick={() => setIsOpen(false)}>
                  ðŸ“Š Dashboard
                </Link>
              </li>
              <li>
                <Link to="/add-expense" className="sidebar-link" onClick={() => setIsOpen(false)}>
                  âž• Add Expense
                </Link>
              </li>
              <li>
                <button onClick={handleLogout} className="sidebar-link logout-btn">
                  ðŸšª Logout
                </button>
              </li>
            </ul>
          </div>
        </div>
      )}
    </>
  );
};

export default Sidebar;
