import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  PieChart, Pie, Cell, LineChart, Line, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [dailyExpenses, setDailyExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  const userName = localStorage.getItem("userName") || "User";

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }

        const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
        setExpenses(sortedExpenses);

        // Calculate total expenses
        setTotalExpenses(sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0));

        // Calculate category totals
        const categoryData = sortedExpenses.reduce((acc, expense) => {
          acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
          return acc;
        }, {});
        setCategoryTotals(Object.entries(categoryData).map(([name, value]) => ({ name, value })));

        // Calculate monthly expenses
        const monthlyData = sortedExpenses.reduce((acc, expense) => {
          const month = new Date(expense.date).toLocaleString("default", { month: "short" });
          acc[month] = (acc[month] || 0) + expense.amount;
          return acc;
        }, {});
        setMonthlyExpenses(Object.entries(monthlyData).map(([month, amount]) => ({ month, amount })));

        // Calculate daily expenses (last 7 days)
        const last7Days = [...Array(7)].map((_, i) => {
          const d = new Date();
          d.setDate(d.getDate() - i);
          return d.toLocaleDateString("en-US", { weekday: 'short' });
        }).reverse();

        const dailyData = last7Days.map(day => {
          const dayExpenses = sortedExpenses.filter(exp => 
            new Date(exp.date).toLocaleDateString("en-US", { weekday: 'short' }) === day
          );
          const total = dayExpenses.reduce((sum, exp) => sum + exp.amount, 0);
          return { day, amount: total };
        });
        setDailyExpenses(dailyData);

        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch expenses");
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [navigate]);

  const formatCurrency = (amount) => new Intl.NumberFormat("en-US", { style: "currency", currency: "USD" }).format(amount);

  const toggleChat = () => {
    setChatOpen(!chatOpen);
    setTimeout(() => {
      const chatBody = document.querySelector('.chat-body');
      if (chatBody) chatBody.scrollTop = chatBody.scrollHeight;
    }, 100);
  };

  const handleMessageSend = async () => {
    if (newMessage.trim() === "") return;

    const userMessage = { sender: "user", text: newMessage };
    setMessages(prev => [...prev, userMessage]);
    setNewMessage("");

    // Simulate AI response
    setTimeout(() => {
      const aiResponses = [
        `Your largest expense category is ${categoryTotals[0]?.name || "unknown"}.`,
        `You've spent ${formatCurrency(totalExpenses)} this period.`,
        "Consider reviewing your spending in the 'Entertainment' category.",
        "Your spending trends show a healthy balance."
      ];
      const aiMessage = {
        sender: "ai",
        text: aiResponses[Math.floor(Math.random() * aiResponses.length)]
      };
      setMessages(prev => [...prev, aiMessage]);

      const chatBody = document.querySelector('.chat-body');
      if (chatBody) chatBody.scrollTop = chatBody.scrollHeight;
    }, 800);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleMessageSend();
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <Sidebar />
        <div className="loading-content">
          <div className="spinner"></div>
          <p>Loading your financial data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h2>Financial Dashboard</h2>
          <div className="greeting">
            <h3>Welcome back, {userName}! 👋</h3>
            <p>Here's your financial overview</p>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        {/* Stats Overview */}
        <div className="stats-grid">
          <div className="stat-card total-expenses">
            <h3>Total Expenses</h3>
            <p className="stat-amount">{formatCurrency(totalExpenses)}</p>
            <div className="stat-trend up">↑ 12% from last month</div>
          </div>
          <div className="stat-card categories">
            <h3>Categories</h3>
            <p className="stat-amount">{categoryTotals.length}</p>
            <div className="stat-trend">
              Top: {categoryTotals[0]?.name || 'N/A'}
            </div>
          </div>
          <div className="stat-card monthly">
            <h3>This Month</h3>
            <p className="stat-amount">{formatCurrency(monthlyExpenses[monthlyExpenses.length - 1]?.amount || 0)}</p>
            <div className="stat-trend down">↓ 5% from target</div>
          </div>
        </div>

        {/* Charts Section */}
        <div className="charts-container">
          {/* Pie Chart */}
          <div className="chart-box">
            <h3>Expense Distribution</h3>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={categoryTotals}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, percent }) => `${(percent * 100).toFixed(0)}%`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {categoryTotals.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </div>

          {/* Line Chart */}
          <div className="chart-box">
            <h3>Monthly Trend</h3>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={monthlyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="amount"
                  stroke="#8884d8"
                  strokeWidth={2}
                  activeDot={{ r: 8 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>

          {/* Bar Chart */}
          <div className="chart-box">
            <h3>Weekly Spending</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={dailyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="day" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
                <Bar 
                  dataKey="amount" 
                  fill="#82ca9d" 
                  radius={[4, 4, 0, 0]} 
                  animationDuration={1500}
                />
              </BarChart>
            </ResponsiveContainer>
          </div>

          {/* Area Chart */}
          <div className="chart-box">
            <h3>Cumulative Spending</h3>
            <ResponsiveContainer width="100%" height={300}>
              <AreaChart data={monthlyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Area
                  type="monotone"
                  dataKey="amount"
                  stroke="#ffc658"
                  fill="#ffc658"
                  fillOpacity={0.6}
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Recent Expenses */}
        <div className="recent-expenses">
          <h3>Recent Transactions</h3>
          <div className="expense-table-container">
            <table className="expense-table">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Category</th>
                  <th>Description</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {expenses.slice(0, 5).map((expense) => (
                  <tr key={expense._id}>
                    <td>{new Date(expense.date).toLocaleDateString()}</td>
                    <td>
                      <span className="category-tag" style={{ 
                        backgroundColor: COLORS[categoryTotals.findIndex(c => c.name === expense.category) % COLORS.length] 
                      }}>
                        {expense.category}
                      </span>
                    </td>
                    <td>{expense.description || 'No description'}</td>
                    <td className={`amount ${expense.amount > 500 ? 'highlight' : ''}`}>
                      {formatCurrency(expense.amount)}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Chat Interface */}
      <div className={`chat-icon ${chatOpen ? 'active' : ''}`} onClick={toggleChat}>
        <div className="chat-bubble">
          <span>💬</span>
          {messages.length > 0 && !chatOpen && <span className="notification-badge">{messages.length}</span>}
        </div>
      </div>

      {chatOpen && (
        <div className="chat-panel">
          <div className="chat-header">
            <h4>Finance Assistant</h4>
            <button className="close-btn" onClick={toggleChat}>
              <span>×</span>
            </button>
          </div>
          <div className="chat-body">
            {messages.length === 0 ? (
              <div className="welcome-message">
                <p>Hello {userName}! How can I help with your finances today?</p>
                <div className="quick-questions">
                  <button onClick={() => setNewMessage("What's my spending trend?")}>
                    What's my spending trend?
                  </button>
                  <button onClick={() => setNewMessage("How can I save more?")}>
                    How can I save more?
                  </button>
                </div>
              </div>
            ) : (
              messages.map((message, index) => (
                <div
                  key={index}
                  className={`chat-message ${message.sender}`}
                >
                  <div className="message-content">
                    {message.text}
                  </div>
                </div>
              ))
            )}
          </div>
          <div className="chat-footer">
            <input
              type="text"
              placeholder="Ask about your finances..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleKeyDown}
            />
            <button 
              className="send-btn"
              onClick={handleMessageSend}
              disabled={!newMessage.trim()}
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
