import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Sidebar from "../components/Sidebar";
import API_BASE_URL from "../config";
import "../styles/Dashboard.css";
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  PieChart, Pie, Cell, ResponsiveContainer
} from "recharts";
import { Doughnut, Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip as ChartTooltip, Legend as ChartLegend, ArcElement } from "chart.js";

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Title, ChartTooltip, ChartLegend, ArcElement);

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return navigate("/login");

        const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
        setExpenses(sortedExpenses);
        setTotalExpenses(sortedExpenses.reduce((sum, exp) => sum + exp.amount, 0));

        const categoryMap = {};
        sortedExpenses.forEach(exp => {
          categoryMap[exp.category] = (categoryMap[exp.category] || 0) + exp.amount;
        });

        setCategoryTotals(Object.entries(categoryMap).map(([name, value]) => ({ name, value })));

        const monthlyMap = {};
        sortedExpenses.forEach(exp => {
          const month = new Date(exp.date).toLocaleString("default", { month: "long" });
          monthlyMap[month] = (monthlyMap[month] || 0) + exp.amount;
        });

        setMonthlyExpenses(Object.entries(monthlyMap).map(([month, amount]) => ({ month, amount })));

        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch expenses");
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [navigate]);

  const formatCurrency = (amount) => new Intl.NumberFormat("en-US", {
    style: "currency", currency: "USD"
  }).format(amount);

  if (loading) {
    return (
      <div className="flex">
        <Sidebar />
        <div className="flex-1 p-6 text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading your financial data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 p-6 bg-gray-100 min-h-screen">
        <div className="max-w-7xl mx-auto">

          {error && <div className="bg-red-100 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}

          {/* Total Expenses Card */}
          <div className="bg-white rounded-lg shadow-lg p-5 text-center hover:shadow-xl transition-all">
            <h3 className="text-lg font-semibold text-gray-600">Total Expenses</h3>
            <p className="text-3xl font-bold text-black">{formatCurrency(totalExpenses)}</p>
          </div>

          {/* Charts Layout */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
            {/* Doughnut & Pie Charts Side by Side */}
            <div className="grid grid-cols-2 gap-4">
              {/* Doughnut Chart */}
              <div className="bg-white rounded-lg shadow-lg p-5">
                <h3 className="text-lg font-semibold text-gray-800 mb-3">Expense Breakdown</h3>
                <Doughnut data={{
                  labels: categoryTotals.map(item => item.name),
                  datasets: [{ data: categoryTotals.map(item => item.value), backgroundColor: COLORS }]
                }} options={{ maintainAspectRatio: false }} />
              </div>

              {/* Pie Chart */}
              <div className="bg-white rounded-lg shadow-lg p-5">
                <h3 className="text-lg font-semibold text-gray-800 mb-3">Category Distribution</h3>
                <ResponsiveContainer width="100%" height={200}>
                  <PieChart>
                    <Pie data={categoryTotals} dataKey="value" cx="50%" cy="50%" outerRadius={80}>
                      {categoryTotals.map((entry, index) => <Cell key={index} fill={COLORS[index % COLORS.length]} />)}
                    </Pie>
                    <Tooltip formatter={(value) => formatCurrency(value)} />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Line & Bar Charts Side by Side */}
            <div className="grid grid-cols-2 gap-4">
              {/* Line Chart */}
              <div className="bg-white rounded-lg shadow-lg p-5">
                <h3 className="text-lg font-semibold text-gray-800 mb-3">Monthly Trends</h3>
                <Line data={{
                  labels: monthlyExpenses.map(item => item.month),
                  datasets: [{ label: "Expenses", data: monthlyExpenses.map(item => item.amount), borderColor: "#FF8042", fill: false }]
                }} options={{ maintainAspectRatio: false }} />
              </div>

              {/* Bar Chart */}
              <div className="bg-white rounded-lg shadow-lg p-5">
                <h3 className="text-lg font-semibold text-gray-800 mb-3">Expense Comparison</h3>
                <ResponsiveContainer width="100%" height={200}>
                  <BarChart data={monthlyExpenses}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="amount" fill="#82ca9d" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>

          {/* Recent Expenses Table */}
          <div className="bg-white rounded-lg shadow-lg p-5 mt-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-3">Recent Expenses</h2>
            <div className="overflow-x-auto">
              <table className="w-full border-collapse border border-gray-200">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="p-3 text-left border">Date</th>
                    <th className="p-3 text-left border">Category</th>
                    <th className="p-3 text-left border">Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {expenses.map((exp) => (
                    <tr key={exp._id} className="hover:bg-gray-50">
                      <td className="p-3 border">{new Date(exp.date).toLocaleDateString()}</td>
                      <td className="p-3 border">{exp.category}</td>
                      <td className="p-3 border">{formatCurrency(exp.amount)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
};

export default Dashboard;
