import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  PieChart, Pie, Cell, LineChart, Line, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [dailyExpenses, setDailyExpenses] = useState([]);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const userName = localStorage.getItem("userName") || "User";

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }

        const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
        setExpenses(sortedExpenses);
        setTotalExpenses(sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0));

        const categoryData = sortedExpenses.reduce((acc, expense) => {
          acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
          return acc;
        }, {});
        setCategoryTotals(Object.entries(categoryData).map(([name, value]) => ({ name, value })));

        const monthlyData = sortedExpenses.reduce((acc, expense) => {
          const date = new Date(expense.date);
          const key = date.toLocaleString("default", { month: "short", year: "numeric" });
          acc[key] = (acc[key] || 0) + expense.amount;
          return acc;
        }, {});
        const monthlyArray = Object.entries(monthlyData).map(([month, amount]) => ({ month, amount }));
        setMonthlyExpenses(monthlyArray);

        const last7Days = [...Array(7)].map((_, i) => {
          const d = new Date();
          d.setDate(d.getDate() - i);
          return d.toLocaleDateString("en-US", { weekday: 'short' });
        }).reverse();

        const dailyData = last7Days.map(day => {
          const dayExpenses = sortedExpenses.filter(exp =>
            new Date(exp.date).toLocaleDateString("en-US", { weekday: 'short' }) === day
          );
          const total = dayExpenses.reduce((sum, exp) => sum + exp.amount, 0);
          return { day, amount: total };
        });
        setDailyExpenses(dailyData);

        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch expenses");
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [navigate]);

  const deleteExpense = async (expenseId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      await axios.delete(`${API_BASE_URL}/finance/expenses/${expenseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setExpenses((prev) => {
        const updatedExpenses = prev.filter((expense) => expense._id !== expenseId);
        setTotalExpenses(updatedExpenses.reduce((sum, expense) => sum + expense.amount, 0));

        const newCategoryData = updatedExpenses.reduce((acc, expense) => {
          acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
          return acc;
        }, {});
        setCategoryTotals(Object.entries(newCategoryData).map(([name, value]) => ({ name, value })));

        return updatedExpenses;
      });
    } catch (err) {
      console.error("Error deleting expense:", err);
    }
  };

  const availableYears = [...new Set(monthlyExpenses.map((entry) => entry.month.split(" ")[1]))].sort((a, b) => b - a);
  const filteredMonthlyExpenses = monthlyExpenses.filter((entry) => entry.month.includes(selectedYear));

  if (loading) {
    return (
      <div className="dashboard-loading">
        <Sidebar />
        <div className="loading-content">
          <div className="spinner"></div>
          <p>Loading your financial data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h2>Financial Dashboard</h2>
          <div className="greeting">
            <h3>Welcome back, {userName}! 👋</h3>
            <p>Here's your financial overview</p>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="stats-grid">
          <div className="stat-card total-expenses">
            <h3>Total Expenses</h3>
            <p className="stat-amount">{formatCurrency(totalExpenses)}</p>
            <div className="stat-trend up">↑ 12% from last month</div>
          </div>
          <div className="stat-card categories">
            <h3>Categories</h3>
            <p className="stat-amount">{categoryTotals.length}</p>
            <div className="stat-trend">
              Top: {categoryTotals[0]?.name || 'N/A'}
            </div>
          </div>
          <div className="stat-card monthly">
            <h3>This Month</h3>
            <p className="stat-amount">{formatCurrency(filteredMonthlyExpenses[filteredMonthlyExpenses.length - 1]?.amount || 0)}</p>
            <div className="stat-trend down">↓ 5% from target</div>
          </div>
        </div>

        <div className="charts-container">
          <div className="chart-box">
            <h3>Expense Distribution</h3>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={categoryTotals}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, percent }) => `${(percent * 100).toFixed(0)}%`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {categoryTotals.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </div>

          <div className="chart-box">
            <div className="flex justify-between items-center mb-2">
              <h3>Monthly Trend</h3>
              <select
                value={selectedYear}
                onChange={(e) => setSelectedYear(e.target.value)}
                className="year-dropdown"
              >
                {availableYears.map((year) => (
                  <option key={year} value={year}>{year}</option>
                ))}
              </select>
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={filteredMonthlyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="amount"
                  stroke="#8884d8"
                  strokeWidth={2}
                  activeDot={{ r: 8 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>

          <div className="chart-box">
            <h3>Weekly Spending</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={dailyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="day" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
                <Bar dataKey="amount" fill="#82ca9d" radius={[4, 4, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>

          <div className="chart-box">
            <h3>Cumulative Spending</h3>
            <ResponsiveContainer width="100%" height={300}>
              <AreaChart data={filteredMonthlyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Area
                  type="monotone"
                  dataKey="amount"
                  stroke="#ffc658"
                  fill="#ffc658"
                  fillOpacity={0.6}
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </div>

        <div className="recent-expenses">
          <h3 style={{ textAlign: "center" }}>Recent Transactions</h3>
          <div className="expense-table-container">
            <table className="expense-table">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Category</th>
                  <th>Description</th>
                  <th>Amount</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {expenses.map((exp) => (
                  <tr key={exp._id}>
                    <td>{new Date(exp.date).toLocaleDateString()}</td>
                    <td>{exp.category}</td>
                    <td>{exp.description}</td>
                    <td>{formatCurrency(exp.amount)}</td>
                    <td>
                      <button className="delete-btn" onClick={() => deleteExpense(exp._id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
