import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  PieChart, Pie, Cell, LineChart, Line, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";
import { Link } from "react-router-dom";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  // State declarations
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [income, setIncome] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [totalIncome, setTotalIncome] = useState(0);
  const [balance, setBalance] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [dailyExpenses, setDailyExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [aiLoading, setAiLoading] = useState(false);

  const userName = localStorage.getItem("userName") || "User";

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
      maximumFractionDigits: 0
    }).format(amount);
  };

  // Fetch expenses data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const [expensesRes, incomeRes] = await Promise.all([
          axios.get("http://localhost:5000/api/expenses", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5000/api/income", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        const expensesData = expensesRes.data;
        const incomeData = incomeRes.data;

        setExpenses(expensesData);
        setIncome(incomeData);

        const totalExp = expensesData.reduce((sum, exp) => sum + exp.amount, 0);
        const totalInc = incomeData.reduce((sum, inc) => sum + inc.amount, 0);
        const bal = totalInc - totalExp;

        setTotalExpenses(totalExp);
        setTotalIncome(totalInc);
        setBalance(bal);

        const categoryData = expensesData.reduce((acc, expense) => {
          acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
          return acc;
        }, {});
        setCategoryTotals(Object.entries(categoryData).map(([name, value]) => ({ name, value })));

        const monthlyData = expensesData.reduce((acc, expense) => {
          const month = new Date(expense.date).toLocaleString("default", { month: "short" });
          acc[month] = (acc[month] || 0) + expense.amount;
          return acc;
        }, {});
        setMonthlyExpenses(Object.entries(monthlyData).map(([month, amount]) => ({ month, amount })));

        const last7Days = [...Array(7)].map((_, i) => {
          const d = new Date();
          d.setDate(d.getDate() - i);
          return d.toLocaleDateString("en-US", { weekday: 'short' });
        }).reverse();

        const dailyData = last7Days.map(day => {
          const dayExpenses = expensesData.filter(exp => 
            new Date(exp.date).toLocaleDateString("en-US", { weekday: 'short' }) === day
          );
          const total = dayExpenses.reduce((sum, exp) => sum + exp.amount, 0);
          return { day, amount: total };
        });
        setDailyExpenses(dailyData);

        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || "Error fetching data");
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Chat functions
  const toggleChat = () => {
    setChatOpen(!chatOpen);
    setTimeout(() => {
      const chatBody = document.querySelector('.chat-body');
      if (chatBody) chatBody.scrollTop = chatBody.scrollHeight;
    }, 100);
  };

  const handleMessageSend = async () => {
    if (!newMessage.trim()) return;

    const userMsg = { sender: 'user', text: newMessage };
    setMessages(prev => [...prev, userMsg]);
    setNewMessage('');
    setAiLoading(true);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("You need to be logged in to use the chat");
      }

      // Prepare expenses data
      const expensesData = categoryTotals.length > 0
        ? categoryTotals.map(c => `${c.name}: ${formatCurrency(c.value)}`).join(', ')
        : 'No expenses data available';

      const response = await axios.post(
        `${API_BASE_URL}/ai/chat`,
        {
          message: newMessage,
          expenses: expensesData
        },
        {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (!response.data.message) {
        throw new Error("No response from AI");
      }

      setMessages(prev => [...prev, { sender: 'ai', text: response.data.message }]);
    } catch (error) {
      console.error("AI error:", error);
      console.error("Error response:", error.response?.data);

      let errorMessage = "Sorry, I can't respond right now.";
      let errorDetails = "";

      if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.response?.data?.error) {
        errorMessage = error.response.data.error;
      } else if (error.message) {
        errorMessage = error.message;
      }

      if (error.response?.data?.details) {
        errorDetails = error.response.data.details;
      }

      setMessages(prev => [...prev, { 
        sender: 'ai', 
        text: errorMessage + (errorDetails ? `\n\nDetails: ${errorDetails}` : ''),
        isError: true 
      }]);
    } finally {
      setAiLoading(false);
      setTimeout(() => {
        const chatBody = document.querySelector('.chat-body');
        if (chatBody) chatBody.scrollTop = chatBody.scrollHeight;
      }, 100);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleMessageSend();
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <Sidebar />
        <div className="loading-content">
          <div className="spinner"></div>
          <p>Loading your financial data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-error">
        <Sidebar />
        <div className="error-content">
          <h2>Error</h2>
          <p>{error}</p>
          <button onClick={() => window.location.reload()}>Refresh Page</button>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h2>Welcome back, {userName}!</h2>
          <p className="greeting">Here's your financial overview</p>
          <div className="dashboard-actions">
            <Link to="/add-income" className="add-income-btn">
              <i className="fas fa-plus"></i>
              Add Income
            </Link>
            <Link to="/add-expense" className="add-expense-btn">
              <i className="fas fa-plus"></i>
              Add Expense
            </Link>
          </div>
        </div>

        <div className="dashboard-stats">
          <div className="stat-card income">
            <div className="stat-icon">💰</div>
            <div className="stat-info">
              <h3>Total Income</h3>
              <p>{formatCurrency(totalIncome)}</p>
            </div>
          </div>

          <div className="stat-card expenses">
            <div className="stat-icon">💸</div>
            <div className="stat-info">
              <h3>Total Expenses</h3>
              <p>{formatCurrency(totalExpenses)}</p>
            </div>
          </div>

          <div className="stat-card balance">
            <div className="stat-icon">⚖️</div>
            <div className="stat-info">
              <h3>Balance</h3>
              <p className={balance >= 0 ? "positive" : "negative"}>
                {formatCurrency(balance)}
              </p>
            </div>
          </div>
        </div>

        <div className="recent-transactions">
          <h3>Recent Transactions</h3>
          <div className="transactions-list">
            {[...expenses, ...income]
              .sort((a, b) => new Date(b.date) - new Date(a.date))
              .slice(0, 5)
              .map((transaction) => (
                <div
                  key={transaction._id}
                  className={`transaction-item ${
                    transaction.amount > 0 ? "income" : "expense"
                  }`}
                >
                  <div className="transaction-icon">
                    {transaction.amount > 0 ? "💰" : "💸"}
                  </div>
                  <div className="transaction-details">
                    <h4>
                      {transaction.amount > 0
                        ? transaction.source
                        : transaction.category}
                    </h4>
                    <p>{new Date(transaction.date).toLocaleDateString()}</p>
                  </div>
                  <div className="transaction-amount">
                    {formatCurrency(transaction.amount)}
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>

      {/* Chat Interface */}
      <div className={`chat-icon ${chatOpen ? 'active' : ''}`} onClick={toggleChat}>
        <div className="chat-bubble">
          <span>💬</span>
          {messages.length > 0 && !chatOpen && <span className="notification-badge">{messages.length}</span>}
        </div>
      </div>

      {chatOpen && (
        <div className="chat-panel">
          <div className="chat-header">
            <h4>Finance Assistant</h4>
            <button className="close-btn" onClick={toggleChat}>
              <span>×</span>
            </button>
          </div>
          <div className="chat-body">
            {messages.length === 0 ? (
              <div className="welcome-message">
                <p>Hello {userName}! How can I help with your finances today?</p>
                <div className="quick-questions">
                  <button onClick={() => setNewMessage("What's my spending trend?")}>
                    What's my spending trend?
                  </button>
                  <button onClick={() => setNewMessage("How can I save more?")}>
                    How can I save more?
                  </button>
                </div>
              </div>
            ) : (
              messages.map((message, index) => (
                <div
                  key={index}
                  className={`chat-message ${message.sender} ${message.isError ? 'error' : ''}`}
                >
                  <div className="message-content">
                    {message.text}
                    {message.sender === 'ai' && index === messages.length - 1 && aiLoading && (
                      <span className="typing-indicator">...</span>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
          <div className="chat-footer">
            <input
              type="text"
              placeholder="Ask about your finances..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              disabled={aiLoading}
            />
            <button 
              className="send-btn"
              onClick={handleMessageSend}
              disabled={!newMessage.trim() || aiLoading}
            >
              {aiLoading ? 'Sending...' : 'Send'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
