import React from "react";
import { Doughnut, Pie, Line, Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend } from "chart.js";
import "./Dashboard.css"; // Import the updated CSS

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

const Dashboard = () => {
  // Sample data for charts
  const doughnutData = {
    labels: ["Food", "Shopping", "Bills", "Entertainment"],
    datasets: [
      {
        data: [30, 25, 20, 25],
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4CAF50"],
      },
    ],
  };

  const pieData = {
    labels: ["Savings", "Investments", "Expenses"],
    datasets: [
      {
        data: [40, 30, 30],
        backgroundColor: ["#FF8C00", "#1E90FF", "#32CD32"],
      },
    ],
  };

  const lineData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
    datasets: [
      {
        label: "Monthly Expenses",
        data: [500, 400, 600, 550, 700, 650],
        borderColor: "#36A2EB",
        borderWidth: 2,
        fill: false,
      },
    ],
  };

  const barData = {
    labels: ["Rent", "Food", "Transport", "Others"],
    datasets: [
      {
        label: "Expense Breakdown",
        data: [800, 500, 300, 200],
        backgroundColor: "#FF6384",
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-content">
        <h2 className="dashboard-header">Dashboard</h2>

        <div className="charts-container">
          {/* Doughnut Chart */}
          <div className="chart-container small">
            <Doughnut
              data={doughnutData}
              options={{
                responsive: true,
                maintainAspectRatio: false, // Prevents expanding
                plugins: {
                  legend: { display: true, position: "bottom" },
                },
              }}
            />
          </div>

          {/* Pie Chart */}
          <div className="chart-container small">
            <Pie
              data={pieData}
              options={{
                responsive: true,
                maintainAspectRatio: false, // Prevents expanding
                plugins: {
                  legend: { display: true, position: "bottom" },
                },
              }}
            />
          </div>

          {/* Line Chart */}
          <div className="chart-container large">
            <Line
              data={lineData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: { display: true, position: "top" },
                },
              }}
            />
          </div>

          {/* Bar Chart */}
          <div className="chart-container large">
            <Bar
              data={barData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: { display: true, position: "top" },
                },
                scales: {
                  y: { beginAtZero: true },
                },
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
