import { useEffect, useState } from "react";
import axios from "axios";
import Sidebar from "../components/Sidebar"; // Import Sidebar from the components folder
import { Link } from "react-router-dom";

const Dashboard = () => {
  const [balance, setBalance] = useState(0);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true); // Start loading state
        const res = await axios.get("http://localhost:5000/api/finance/expenses", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setExpenses(res.data);

        // Calculate the total expense and remaining balance
        const totalExpense = res.data.reduce((sum, exp) => sum + exp.amount, 0);
        setBalance(1000 - totalExpense); // Assume 1000 is total income

      } catch (error) {
        setError("Failed to fetch data. Please try again later.");
        console.error(error);
      } finally {
        setLoading(false); // End loading state
      }
    };
    fetchData();
  }, []);

  return (
    <div className="flex">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content Area */}
      <div className="flex-1 p-6 bg-gray-100 min-h-screen">
        <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-lg">
          <h1 className="text-2xl font-bold text-center">Dashboard</h1>

          {loading ? (
            <div className="text-center mt-4">Loading...</div>
          ) : error ? (
            <div className="text-center mt-4 text-red-500">{error}</div>
          ) : (
            <>
              <div className="mt-4">
                <h2 className="text-lg font-semibold">Balance</h2>
                <p className="text-xl font-bold text-green-600">${balance}</p>
              </div>

              <div className="mt-6">
                <h2 className="text-lg font-semibold">Expenses</h2>
                <ul className="mt-2">
                  {expenses.length === 0 ? (
                    <p className="text-center text-gray-500">No expenses recorded.</p>
                  ) : (
                    expenses.map((expense) => (
                      <li key={expense._id} className="border p-3 mt-2 rounded-md shadow-sm">
                        <div className="flex justify-between items-center">
                          <span className="text-gray-800">{expense.category}</span>
                          <span className="text-gray-600">${expense.amount}</span>
                        </div>
                      </li>
                    ))
                  )}
                </ul>
              </div>
            </>
          )}

          <div className="mt-6 text-center">
            <Link
              to="/add-expense"
              className="inline-block px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              Add Expense
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
