import { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [balance, setBalance] = useState(0);
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/finance/expenses", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setExpenses(res.data);
        const totalExpense = res.data.reduce((sum, exp) => sum + exp.amount, 0);
        setBalance(1000 - totalExpense); // Assume 1000 is total income
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">Dashboard</h1>
      <p className="text-lg">Balance: ${balance}</p>
      <h2 className="text-xl mt-4">Expenses</h2>
      <ul>
        {expenses.map((expense) => (
          <li key={expense._id} className="border p-2 mt-2">
            {expense.category}: ${expense.amount}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
