import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Sidebar from "../components/Sidebar";
import API_BASE_URL from "../config";

const Dashboard = () => {
  const [balance, setBalance] = useState(0);
  const [income, setIncome] = useState(1000);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is authenticated
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        const res = await axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setExpenses(res.data);

        // Calculate total expenses
        const totalExpense = res.data.reduce((sum, exp) => sum + exp.amount, 0);
        setBalance(income - totalExpense);
      } catch (error) {
        if (error.response?.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
        } else {
          setError(error.response?.data?.message || "Failed to fetch data.");
        }
        console.error("Error fetching expenses:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [income, navigate]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />

      <main className="flex-1 p-6 overflow-auto">
        <div className="max-w-4xl mx-auto">
          <header className="bg-white p-6 rounded-lg shadow-lg mb-6">
            <h1 className="text-3xl font-bold text-gray-800 mb-4">Financial Overview</h1>
            <div className="grid grid-cols-2 gap-4">
              <div className="p-4 bg-green-50 rounded-lg">
                <h2 className="text-lg font-semibold text-gray-700">Total Income</h2>
                <p className="text-2xl font-bold text-green-600">{formatCurrency(income)}</p>
              </div>
              <div className="p-4 bg-blue-50 rounded-lg">
                <h2 className="text-lg font-semibold text-gray-700">Current Balance</h2>
                <p className={`text-2xl font-bold ${balance < 0 ? "text-red-600" : "text-blue-600"}`}>
                  {formatCurrency(balance)}
                </p>
              </div>
            </div>
          </header>

          {loading ? (
            <div className="text-center p-8 bg-white rounded-lg shadow-lg">
              <div className="animate-spin h-8 w-8 border-4 border-blue-600 border-t-transparent rounded-full mx-auto"></div>
              <p className="mt-4 text-gray-600">Loading your financial data...</p>
            </div>
          ) : error ? (
            <div className="p-4 bg-red-50 text-red-700 rounded-lg shadow">
              <p>{error}</p>
            </div>
          ) : (
            <section className="bg-white p-6 rounded-lg shadow-lg">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold text-gray-800">Recent Expenses</h2>
                <button
                  onClick={() => navigate("/add-expense")}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Add New Expense
                </button>
              </div>

              {expenses.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <p>No expenses recorded yet.</p>
                  <p className="mt-2 text-sm">Start tracking your expenses by adding one!</p>
                </div>
              ) : (
                <ul className="divide-y divide-gray-200">
                  {expenses.map((expense) => (
                    <li key={expense._id} className="py-3 flex justify-between items-center hover:bg-gray-50 px-4 rounded">
                      <div>
                        <span className="font-medium text-gray-800">{expense.category}</span>
                        <span className="text-sm text-gray-500 ml-2">
                          {new Date(expense.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      <span className="font-semibold text-gray-700">{formatCurrency(expense.amount)}</span>
                    </li>
                  ))}
                </ul>
              )}
            </section>
          )}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
