import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", { email, password });
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="bg-white p-6 rounded-lg shadow-lg w-80">
        <h2 className="text-xl font-bold mb-4 text-center">Login</h2>
        <form onSubmit={handleLogin} className="flex flex-col gap-3">
          <input type="email" placeholder="Email" className="border p-2 rounded-md" onChange={(e) => setEmail(e.target.value)} />
          <input type="password" placeholder="Password" className="border p-2 rounded-md" onChange={(e) => setPassword(e.target.value)} />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
