import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";
import "../styles/Sidebar.css";

const Sidebar = () => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <>
      {/* Menu Button */}
      <button
        className="menu-button"
        onClick={() => {
          console.log("Menu Button Clicked");
          setIsOpen(!isOpen);
        }}
        style={{
          position: "fixed",
          top: "20px",
          left: "20px",
          zIndex: 1001,
          cursor: "pointer",
        }}
      >
        <FaBars />
      </button>

      {/* Sidebar */}
      {isOpen && (
        <div
          className="sidebar-overlay"
          onClick={() => {
            console.log("Closing Sidebar");
            setIsOpen(false);
          }}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0,0,0,0.5)",
            zIndex: 999,
          }}
        >
          <div
            className="sidebar"
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside sidebar
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "250px",
              height: "100%",
              backgroundColor: "#2c3e50",
              zIndex: 1000,
              padding: "20px",
            }}
          >
            <button
              className="close-btn"
              onClick={() => {
                console.log("Closing Sidebar with X button");
                setIsOpen(false);
              }}
              style={{
                position: "absolute",
                top: "15px",
                right: "15px",
                background: "none",
                border: "none",
                color: "white",
                cursor: "pointer",
              }}
            >
              <FaTimes />
            </button>

            <div className="sidebar-logo">
              <h2>Finance Tracker</h2>
            </div>
            <ul className="sidebar-list">
              <li>
                <Link
                  to="/dashboard"
                  className="sidebar-link"
                  onClick={() => setIsOpen(false)}
                >
                  ðŸ“Š Dashboard
                </Link>
              </li>
              <li>
                <Link
                  to="/add-expense"
                  className="sidebar-link"
                  onClick={() => setIsOpen(false)}
                >
                  âž• Add Expense
                </Link>
              </li>
              <li>
                <button
                  onClick={handleLogout}
                  className="sidebar-link logout-btn"
                >
                  ðŸšª Logout
                </button>
              </li>
            </ul>
          </div>
        </div>
      )}
    </>
  );
};

export default Sidebar;
