// src/pages/Signup.js
import React, { useState } from "react";
import "../styles/LoginSignup.css";
import { Link } from "react-router-dom"; // Import Link component

const Signup = () => {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setUserData({
      ...userData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle signup logic here
    console.log("User signed up:", userData);
  };

  return (
    <div className="auth-container">
      {/* Navbar visible on Signup page */}
      <nav className="bg-black p-4">
        <h1 className="text-3xl text-gold font-bold mb-4 text-center">Fitness Assistant</h1>
        <div className="flex justify-center space-x-4">
          <Link to="/" className="text-white text-lg hover:text-gold">Home</Link>
          <Link to="/login" className="text-white text-lg hover:text-gold">Login</Link>
          <Link to="/signup" className="text-white text-lg hover:text-gold">Sign Up</Link>
          <Link to="/add-expense" className="text-white text-lg hover:text-gold">Add Expense</Link>
          <Link to="/dashboard" className="text-white text-lg hover:text-gold">Dashboard</Link>
        </div>
      </nav>

      <div className="auth-form">
        <h2 className="form-title">Sign Up</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="input-label">Name</label>
            <input
              type="text"
              name="name"
              className="input-field"
              value={userData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="input-label">Email</label>
            <input
              type="email"
              name="email"
              className="input-field"
              value={userData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="input-label">Password</label>
            <input
              type="password"
              name="password"
              className="input-field"
              value={userData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="submit-btn">Sign Up</button>
        </form>

        {/* Add link to login page */}
        <div className="mt-4 text-center">
          <p className="text-sm">
            Already have an account?{" "}
            <Link to="/login" className="text-blue-500 hover:underline">Login</Link>
          </p>
        </div>

        {/* Add Home button */}
        <div className="mt-4 text-center">
          <Link to="/" className="text-blue-500 hover:underline">Go to Home</Link>
        </div>
      </div>
    </div>
  );
};

export default Signup;
