// src/App.js
import React from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import Dashboard from "./pages/Dashboard";
import AddExpense from "./pages/AddExpense";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Welcome from "./pages/Welcome"; // Importing the Welcome page
import Navbar from "./components/Navbar"; // Import Navbar component
import Sidebar from "./components/Sidebar"; // Import Sidebar component

const App = () => {
  return (
    <Router>
      <AppContent />
    </Router>
  );
};

const AppContent = () => {
  const location = useLocation();

  // Check if the current page is the Welcome, Login, or Signup page
  const isRestrictedPage = location.pathname === "/" || location.pathname === "/login" || location.pathname === "/signup";

  return (
    <div className="container mx-auto">
      {/* Only show Navbar and Sidebar if not on the Welcome, Login or Signup page */}
      {!isRestrictedPage && (
        <>
          <Navbar />
          <div className="flex">
            <Sidebar />
            {/* Main Content */}
            <div className="main-content flex-1 p-6">
              <Routes>
                <Route path="/add-expense" element={<AddExpense />} />
                <Route path="/dashboard" element={<Dashboard />} />
              </Routes>
            </div>
          </div>
        </>
      )}

      {/* Welcome, Login, Signup pages (without Navbar and Sidebar) */}
      {isRestrictedPage && (
        <div className="main-content p-6">
          <Routes>
            <Route path="/" element={<Welcome />} /> {/* Welcome page as homepage */}
            <Route path="/login" element={<Login />} /> {/* Login page */}
            <Route path="/signup" element={<Signup />} /> {/* Signup page */}
          </Routes>
        </div>
      )}
    </div>
  );
};

export default App;
