// src/App.js
import React from "react";
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from "react-router-dom";
import Dashboard from "./pages/Dashboard";
import AddExpense from "./pages/AddExpense";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Welcome from "./pages/Welcome";  // Importing the Welcome page
import Navbar from "./components/Navbar"; // Import Navbar component
import Sidebar from "./components/Sidebar"; // Import Sidebar component

const App = () => {
  const location = useLocation();

  // Check if the current page is the Welcome page
  const isWelcomePage = location.pathname === "/";

  return (
    <Router>
      <div className="container mx-auto">
        {/* Only show Navbar and Sidebar if not on the Welcome page */}
        {!isWelcomePage && (
          <>
            <Navbar />
            <div className="flex">
              <Sidebar />
              {/* Main Content */}
              <div className="main-content flex-1 p-6">
                <Routes>
                  <Route path="/login" element={<Login />} />
                  <Route path="/signup" element={<Signup />} />
                  <Route path="/add-expense" element={<AddExpense />} />
                  <Route path="/dashboard" element={<Dashboard />} />
                </Routes>
              </div>
            </div>
          </>
        )}

        {/* Welcome page (without Navbar and Sidebar) */}
        {isWelcomePage && (
          <div className="main-content p-6">
            <Routes>
              <Route path="/" element={<Welcome />} /> {/* Welcome page as homepage */}
            </Routes>
          </div>
        )}
      </div>
    </Router>
  );
};

export default App;
