import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaHome, FaChartPie, FaBell, FaUser } from "react-icons/fa";
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, PieChart, Pie, Cell, ResponsiveContainer } from "recharts";
import API_BASE_URL from "../config";
import "../styles/Dashboard.css";

const COLORS = ["#0088FE", "#FF8042", "#00C49F"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setExpenses(sortedExpenses);

      const total = sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0);
      setTotalExpenses(total);

      const categoryMap = {};
      sortedExpenses.forEach((expense) => {
        categoryMap[expense.category] = (categoryMap[expense.category] || 0) + expense.amount;
      });

      setCategoryTotals(Object.entries(categoryMap).map(([name, value]) => ({ name, value })));

      const monthlyMap = {};
      sortedExpenses.forEach((expense) => {
        const month = new Date(expense.date).toLocaleString("default", { month: "short" });
        monthlyMap[month] = (monthlyMap[month] || 0) + expense.amount;
      });

      setMonthlyExpenses(Object.entries(monthlyMap).map(([month, amount]) => ({ month, amount })));

      setLoading(false);
    } catch (err) {
      setError("Failed to fetch expenses");
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-20 bg-white shadow-md flex flex-col items-center py-6 space-y-6">
        <FaHome className="text-gray-600 text-2xl cursor-pointer" />
        <FaChartPie className="text-gray-600 text-2xl cursor-pointer" />
        <FaBell className="text-gray-600 text-2xl cursor-pointer" />
        <FaUser className="text-gray-600 text-2xl cursor-pointer" />
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-8">
        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Hi, <span className="text-blue-600">Prajwal</span></h1>
          <div className="flex items-center space-x-4">
            <FaBell className="text-gray-500 text-xl cursor-pointer" />
            <img src="/profile.jpg" alt="Profile" className="w-10 h-10 rounded-full" />
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-3 gap-6">
          <div className="bg-blue-500 text-white p-6 rounded-xl shadow-lg">
            <p className="text-lg">Total Expenses</p>
            <h2 className="text-3xl font-bold">${totalExpenses}</h2>
          </div>
          <div className="bg-red-500 text-white p-6 rounded-xl shadow-lg">
            <p className="text-lg">Categories</p>
            <h2 className="text-3xl font-bold">{categoryTotals.length}</h2>
          </div>
          <div className="bg-yellow-500 text-white p-6 rounded-xl shadow-lg">
            <p className="text-lg">This Month</p>
            <h2 className="text-3xl font-bold">${monthlyExpenses[monthlyExpenses.length - 1]?.amount || 0}</h2>
          </div>
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-2 gap-6 mt-8">
          {/* Pie Chart */}
          <div className="bg-white p-6 rounded-xl shadow-lg">
            <h3 className="text-lg font-semibold mb-4">Expense by Category</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie data={categoryTotals} cx="50%" cy="50%" outerRadius={80} dataKey="value">
                  {categoryTotals.map((_, index) => (
                    <Cell key={index} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </div>

          {/* Bar Chart */}
          <div className="bg-white p-6 rounded-xl shadow-lg">
            <h3 className="text-lg font-semibold mb-4">Monthly Expenses</h3>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={monthlyExpenses}>
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="amount" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Add Expense Button */}
        <div className="text-center mt-8">
          <button onClick={() => navigate("/add-expense")} className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:scale-105 transition">
            Add Expense
          </button>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
