import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const navigate = useNavigate();
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle sending messages
  const handleSendMessage = async () => {
    if (!message.trim()) return;

    // Add user message to chat history
    setChatHistory([...chatHistory, { sender: "user", message }]);
    setMessage(""); // Clear the input field

    try {
      setLoading(true);
      // Send the message to the backend API
      const response = await axios.post("http://localhost:5001/api/chat", { message });

      // Add the AI's response to the chat history
      setChatHistory(prevChat => [...prevChat, { sender: "ai", message: response.data.reply }]);
      setLoading(false);
    } catch (err) {
      setError("Failed to get response from AI");
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <h2>Dashboard</h2>

        {/* Add Chat Section */}
        <div className="chat-section">
          <h3>AI Chat</h3>
          <div className="chat-box">
            {chatHistory.map((chat, index) => (
              <div
                key={index}
                className={`chat-message ${chat.sender}`}
              >
                <p>{chat.message}</p>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Type your message..."
            />
            <button onClick={handleSendMessage} disabled={loading}>
              {loading ? "Loading..." : "Send"}
            </button>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        {/* Existing content like financial stats, charts, etc. */}
        {/* Your existing dashboard content goes here */}
      </div>
    </div>
  );
};

export default Dashboard;
